    private void btn_excluir_alunoActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btn_excluir_alunoActionPerformed
        int resposta = JOptionPane.showConfirmDialog(null, "Deseja realmente excluir?", "Exclusão", JOptionPane.YES_NO_OPTION);

        if(resposta == JOptionPane.YES_NO_OPTION){
            AlunoDAO aDAO = new AlunoDAO();
            aDAO.excluir(Integer.parseInt(txt_RA_aluno2.getText()));
            limparFormularioAluno2();
        }
    }//GEN-LAST:event_btn_excluir_alunoActionPerformed

