    private void btn_consultar_alunoActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btn_consultar_alunoActionPerformed
        int raAluno = Integer.parseInt(txt_RA_aluno.getText());
        AlunoDAO aDAO = new AlunoDAO();

        Aluno a = aDAO.getAluno(raAluno);

        if(a == null){
            limparFormularioAluno2();
            JOptionPane.showMessageDialog(this, "Pessoa não encontrada!");
        } else{
            txt_RA_aluno2.setText(String.valueOf(a.getRa()));
            txt_media_aluno2.setText(String.valueOf(a.getMedia()));
            txt_faltas_aluno2.setText(String.valueOf(a.getFaltas()));
            if(a.getSexo().equals("M")){
                rdo_masculino2_aluno.setSelected(true);
            } else{
                rdo_feminino2_aluno.setSelected(true);
            }
            //txt_idioma.setText(p.getIdioma());
            cmb_idioma_aluno2.setSelectedItem(p.getIdioma());
        }

    }//GEN-LAST:event_btn_consultar_alunoActionPerformed

