    private void btn_salvar_alunoActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btn_salvar_alunoActionPerformed
        String sexo = null;

        if(rdo_masculino_aluno.isSelected()){
            sexo = "M";
            /*JOptionPane.showMessageDialog(null, "Sexo selecionado: "+ sexo, "Alerta!", JOptionPane.OK_OPTION);
            */
        } else if(rdo_feminino_aluno.isSelected()){
            sexo = "F";
            /*JOptionPane.showMessageDialog(null, "Sexo selecionado: "+ sexo, "Alerta!", JOptionPane.OK_OPTION);
            */
        }

        /*if(txt_nome.getText().equals("")){

        }*/
        Aluno a = new Aluno();
        a.setRa(Integer.parseInt(txt_ra_aluno.getText()));
        a.setMedia(Float.parseFloat(txt_media_aluno.getText()));
        a.setFaltas(Integer.parseInt(txt_faltas_aluno.getText()));
        a.setSexo(sexo);
        a.setIdioma(cmb_idioma_aluno.getSelectedItem().toString());

        AlunoDAO aDAO = new AlunoDAO();
        aDAO.inserir(a);
        limparFormularioAluno();

    }//GEN-LAST:event_btn_salvar_alunoActionPerformed

